import React from 'react';
import styled from 'styled-components';
import Link from './Link.js';
import  Logo  from './logo.svg';
import './Header.css';
import HeaderRightColumn from './HeaderRight.js'


const Header = styled.div`
  max-width: 1170px;
  height: 120vh;
  margin: 0 auto;
  font-family: 'Nunito Sans', sans-serif;

`;
const HeaderContent = styled.div`
    display:flex;
    justify-content: space-between;
`
const HeaderLeft = styled.div`
  width: 585px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  position: relative;
`;



const Logotype = styled.h2`
    color: #6400e4;
    font-size: 23px;
    display: flex;
    align-self: center;
    font-weight: 400;
`;
const Span = styled.span`
    align-items: center;
    display: flex;
    font-size: 26px;
    letter-spacing: 0.1em;
`;
const Title = styled.h1`
    font-size: 64px;
    font-weight: 800;
    position: relative;
    font-family: 'Nunito Sans', sans-serif;
`;

const SubTitle = styled.h3`
    font-size: 20px;
    margin-bottom: 0;

`

const Paragraf = styled.p`
    font-size: 20px;
    margin: 0 0 32px 0;
    line-height: 1.9em;
    font-family: 'Nunito Sans', sans-serif;
    display: flex;
    width:100%;

`;


const Button = styled.button`
  background-color: #6400e4;
  border: none;
  color: #fff;  
  padding: 6px 16px;
  font-weight: 700;
  font-size: 16px;
  width: 332px;
  height: 56px;
  border-radius: 50px;
  font-family: 'Nunito Sans', sans-serif;
`;

function LandingHeader() {
  return (

        <Header>
            <Logotype>
               <img className="logotitle" src={Logo} alt=''/> <Span>/PRO</Span>
            </Logotype>
        <HeaderContent>
            <HeaderLeft>
                <Title>The <span className='underline'>Easiest </span>Way to Learn to Code</Title>
                <Paragraf> Unlock Codecademyâ€™s premium features and get where you're going, faster.</Paragraf>
                <Paragraf><Button>Upgrade Now</Button></Paragraf>
                <Paragraf>On a team? Explore <Link link="Codeacademy for Bussines"/> </Paragraf>
            </HeaderLeft>



            <HeaderRightColumn/>
        </HeaderContent>
      </Header>

  );
}
export default LandingHeader;
