import React from 'react';
import styled from 'styled-components';
import Girl from '../img/svg/girl.webp';





const Container = styled.div`
    width: 1900px;
    height: 680px;
    padding: 50px 0;
    margin: 0 0 80px;
    position: relative;
    text-align: center;
    background: #f0f3ff;

`

const Box = styled.div`
    width: 1140px;
    height: 680px;
    text-align: left;
    padding: 0 10px;
    margin: 0 315px 0;
    position: relative;
    img{
        position: absolute;
        top: 0;
        bottom: 0;
        margin: 0 auto;
    }
`


class Illustration extends React.Component{
    render(){
        return(
            <Container >
                <Box >
                    <img src={Girl} alt="girl"/>

                </Box>
            </Container>
        );
    }

}

export default Illustration;
