import React from 'react';
import styled from 'styled-components';
import Link from './Link.js';
import  Logo  from './logo.svg';
import './Header.css';

const Header = styled.div`
  max-width: 1170px;
  height: 120vh;
  align-items: center;
  margin: 0 auto;
`;

const HeaderLeft = styled.div`
  width: 50%;
  text-align: left;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Logotype = styled.h2`
    color: #6400e4;
    font-size: 25px;
    margin: 10px;

`;
const Span = styled.span`
    padding-bottom: 30px;
`;
const Title = styled.h1`
    font-size: 64px;
`;

const Paragraf = styled.p`
    font-size: 19px;
    margin: 0 0 32px;
`;

const HeaderRight = styled.div`
  width: 50%;
  text-align: right;
  padding: 26px 20px;
  display: flex;
  flex-diraction: space-between;
  align-items: center;
  font-size: 16px;
  font-weight: 700;
`;

const Button = styled.button`
  background-color: #6400e4;
  border: none;
  color: #fff;
  padding: 6px 16px;
  font-weight: 700;
  font-size: 16px;
  width: 332px;
  height: 56px;
  border-radius: 50px;
`;

function LandingHeader() {
  return (

      <Header>
        <HeaderLeft>

            <Logotype>
               <img className="logotitle" src={Logo} alt=''/> <Span>/PRO</Span>
            </Logotype>
            <Title>The Easiest Way to Learn to Code</Title>
            <Paragraf> Unlock Codecademyâ€™s premium features and get where you're going, faster.</Paragraf>
            <Button>Upgrade Now</Button>
            <Link link="Codeacademy for Bussines"/>
        </HeaderLeft>


        <HeaderRight>

        </HeaderRight>

      </Header>

  );
}
export default LandingHeader;
